    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
 try{
        double a = Double.parseDouble(jTextField1.getText());
      double b = Double.parseDouble(jTextField2.getText());
      double c = Double.parseDouble(jTextField3.getText());

      // Calculate discriminant
      double discriminant = b*b -4*a*c;

      if (discriminant > 0) {
        // Two real and distinct roots
        double root1 = (-b + Math.sqrt(discriminant)) / (2 * a);
        double root2 = (-b - Math.sqrt(discriminant)) / (2 * a);
        jTextField4.setText(String.valueOf(root1));
        jTextField5.setText(String.valueOf(root2));
      } else if (discriminant == 0) {
        // One real and repeated root
        double root = -b / (2 * a);
        jTextField4.setText(String.valueOf(root));
        jTextField5.setText(String.valueOf(root));
      } if (discriminant < 0) {
        // No real roots
        jOptionPane1.showMessageDialog(rootPane, "No real roots!");
      }
 }
      catch (NumberFormatException ex) {
          jOptionPane1.showMessageDialog(rootPane, "Those aren't numbers homie!!!");      
      }
 catch (ArithmeticException ex) {
     jOptionPane2.showMessageDialog(rootPane, "Can't divide by zero :( Ensure a is not equal to Zero");
 }
            
       // TODO add your handling code here:
    }//GEN-LAST:event_jButton1ActionPerformed

