    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
try{
        double x1 = Double.parseDouble(jTextField1.getText());
double y1 = Double.parseDouble(jTextField2.getText());
double x2 = Double.parseDouble(jTextField3.getText());
double y2 = Double.parseDouble(jTextField4.getText());


if (x2 == x1) {
          jOptionPane1.showMessageDialog(rootPane, "Cannot divide by zero. Please ensure x1 is not equal to x2.");
        } else {
          // Calculate slope
          double slope = (y2 - y1) / (x2 - x1);
          jLabel6.setText("Result (m)=" + slope);

        }
}
catch (NumberFormatException ex){
    jOptionPane1.showMessageDialog(rootPane, "Those aren't numbers homie!!!");
}
   // TODO add your handling code here:
    }//GEN-LAST:event_jButton1ActionPerformed

